				fields = AllFields;
			}
			object[] parentParam = new object[] { parentId,parentId };
			string sql = @"select {0},ChildCount from SiteManage_District as a left join
			(select ParentID as __ParentID,count(1) as ChildCount from SiteManage_District where ParentID in(
			SELECT  ID  FROM SiteManage_District where  ParentID=?) group by ParentID) as b
			on a.ID=b.__ParentID where  a.ParentID=?";
