        /// <summary>
        /// 更新为树级实体列表
        /// </summary>
        /// <param name="list">实体列表</param>
        /// <returns>树级实体列表</returns>
		public List<District> ModelListToTree(List<District> list)
		{
			//查找父节点
			List<District> rootType = list.Where(o => o.ParentID == 0).OrderBy(o => o.OrderID ).ToList();
			//递归主函数
			Action<District> addChild = null;
			addChild = (info =>
				{
					var childInfo = list.Where(o => o.ParentID == info.ID).OrderBy(o => o.OrderID );
					if (childInfo.Count() == 0)
						return;
					childInfo.All(o =>
					{
						if (info.Children == null)
						{
							info.Children=new List<District>();
						}
						info.Children.Add(o);
						addChild(o);
						return true;
					});
				});
			//递归调用
			rootType.ForEach(o => { addChild(o); });
			return rootType;
		}
		
