		public virtual DataTable GetAsyncTreeList(object parentId,string fields="")
		{
			if (string.IsNullOrEmpty(fields))
			{
				fields = AllFields;
			}
			object[] parentParam = new object[] { parentId,parentId };
			string sql = @"select {0},ChildCount from SiteManage_Menu as a left join
			(select ParentID as __ParentID,count(1) as ChildCount from SiteManage_Menu where ParentID in(
			SELECT  MenuID  FROM SiteManage_Menu where  ParentID=?) group by ParentID) as b
			on a.MenuID=b.__ParentID where  a.ParentID=?";
			sql = string.Format(sql, fields);
			return Db.ExecuteDataTableSqlEx(sql, parentParam);
		}
        /// <summary>
        /// 更新为树级实体列表
        /// </summary>
        /// <param name="list">实体列表</param>
        /// <returns>树级实体列表</returns>
		public List<Menu> ModelListToTree(List<Menu> list)
		{
			//查找父节点
			List<Menu> rootType = list.Where(o => o.ParentID == "").OrderBy(o => o.OrderID ).ToList();
			//递归主函数
			Action<Menu> addChild = null;
			addChild = (info =>
				{
					var childInfo = list.Where(o => o.ParentID == info.MenuID).OrderBy(o => o.OrderID );
					if (childInfo.Count() == 0)
						return;
					childInfo.All(o =>
					{
						if (info.Children == null)
						{
							info.Children=new List<Menu>();
						}
						info.Children.Add(o);
						addChild(o);
						return true;
					});
				});
			//递归调用
			rootType.ForEach(o => { addChild(o); });
			return rootType;
		}
		
