                     default:
                     {
                        table = dal.GetAllEntityTable(fields);
                        break;
                     }
                }
                TreeHelper treeHelper = new TreeHelper("MenuID", "MenuName", "OrderID", "ParentID");

                string append="";
                int iscombo = RequestHelper.GetRequestInt("iscombo", 0);
