                rootName=StringHelper.ToJson(rootName);
                if (iscombo == 1)
                {
                    append="{\"id\":\"\",\"text\":\"根目录\"},";
                }
                else if(iscombo==0)
                {
                    append="{\"id\":\"\",\"text\":\"请选择\"},";
                }else if(iscombo==2)
                {
                    append="{\"id\":\"\",\"text\":"+rootName+"},";
                }
                string json= treeHelper.GetTreeJson(table, "", value, append, attributes);
                _jsonsb.Append(json);
            }
            catch (Exception ex)
            {
                _jsonsb = new StringBuilder();
                _jsonsb.Append("{\"total\":\"-1\",\"msg\":" + StringHelper.ToJson(ex.Message) + ",\"rows\":[]}");
            }
