        /// <summary>
        /// 从Excel导入
        /// </summary>
        /// <returns></returns>
        public ActionResult ImportFromExcel()
        {
            Hashtable hash = new Hashtable();
            hash["success"] = false;
            try
            {
                HttpPostedFileBase file = (HttpPostedFileBase)Request.Files["ExcelFile"];
                string savePath = RequestHelper.GetMapPath("~/Runtime/");
                if (!IOHelper.IsExist(savePath, FsoMethod.Folder))
                {
                    IOHelper.Create(savePath, FsoMethod.Folder);
                }
                string saveFileName = "Form_" + RandomHelper.GetRandomNum(10) + ".xls";
                if (file != null)
                {
                    if (file.FileName.LastIndexOf(".") != -1)
                    {
                        string fileFix = file.FileName.Substring(file.FileName.LastIndexOf("."));
                        if (fileFix != ".xls" && fileFix != ".xlsx")
                        {
                            hash["message"] = "请确定上传的是.xls或.xlsx文件！";
                            return Json(hash);
                        }
                        file.SaveAs(savePath + saveFileName);
                    }
                }
                DataSet ds = ExcelHelper.ImportToDataSet(savePath + saveFileName);

               
                int result = dal.ImportDataFromDataSet(ds);
                hash["success"] = result > 0 ? true : false;
            }
            catch (Exception ex)
            {
                hash["message"] = ex.Message;
            }
            return Json(hash);
