		public virtual DataTable GetAsyncTreeList(object parentId,string fields="")
		{
			if (string.IsNullOrEmpty(fields))
			{
				fields = AllFields;
			}
			object[] parentParam = new object[] { parentId,parentId };
			string sql = @"select {0},ChildCount from UC_Department as a left join
			(select ParentID as __ParentID,count(1) as ChildCount from UC_Department where ParentID in(
			SELECT  DepartmentID  FROM UC_Department where  ParentID=?) group by ParentID) as b
			on a.DepartmentID=b.__ParentID where  a.ParentID=?";
			sql = string.Format(sql, fields);
			return Db.ExecuteDataTableSqlEx(sql, parentParam);
		}
        /// <summary>
        /// 更新为树级实体列表
        /// </summary>
        /// <param name="list">实体列表</param>
        /// <returns>树级实体列表</returns>
		public List<Department> ModelListToTree(List<Department> list)
		{
			//查找父节点
			List<Department> rootType = list.Where(o => o.ParentID == 0).OrderBy(o => o.OrderID ).ToList();
			//递归主函数
			Action<Department> addChild = null;
			addChild = (info =>
				{
					var childInfo = list.Where(o => o.ParentID == info.DepartmentID).OrderBy(o => o.OrderID );
					if (childInfo.Count() == 0)
						return;
					childInfo.All(o =>
					{
						if (info.Children == null)
						{
							info.Children=new List<Department>();
						}
						info.Children.Add(o);
						addChild(o);
						return true;
					});
				});
			//递归调用
			rootType.ForEach(o => { addChild(o); });
			return rootType;
		}
		
