        /// <summary>
        /// 保存编辑树
        /// </summary>
        /// <param name="id"></param>
        /// <param name="targetId"></param>
        /// <param name="point"></param>
        /// <returns></returns>
        public int SaveEditTree(int id, string targetId, string point)
        {
            Department targetModel = GetModelByID(targetId);
            int parentId=0;
            int orderId = 0;
            if (point == "append")
            {
                parentId = targetModel.DepartmentID ?? 0;
                orderId = 1;
            }
            else if (point == "top")
            {
                parentId = targetModel.ParentID ?? 0;
                orderId = DataConverter.ToInt(targetModel.OrderID) - 1;
            }
            else if (point == "bottom")
            {
                parentId = targetModel.ParentID ?? 0;
                orderId = DataConverter.ToInt(targetModel.OrderID) + 1;
            }
            Department model=new Department();
            model.DepartmentID = id;
            model.OrderID = orderId;
            model.ParentID = parentId;
            return UpdateByModel(model);
        }    }
